function rangeSlider(e){let t=e.querySelectorAll(".no-ui-slider__ranges")[0],i=e.querySelectorAll(".no-ui-slider__value")[0],c=e.getAttribute("data-min"),l=e.getAttribute("data-max"),n=[e.getAttribute("data-from"),e.getAttribute("data-to")],r=e.querySelectorAll(".no-ui-slider__input")[0];return noUiSlider.create(t,{start:n,step:1,connect:!0,keyboardDefaultStep:1,range:{min:Number(c),max:Number(l)}}),t.noUiSlider.on("update",function(e,t){i.innerHTML=`${e[1].split(".")[0]}%`,r.setAttribute("value",e[1].split(".")[0])}),t}function customSelect(e){let t=e.querySelectorAll(".custom-select__head")[0],i=e.querySelectorAll(".custom-select__body")[0],c=e.querySelectorAll("input");t.addEventListener("click",function(){t.classList.contains("active")?(t.classList.remove("active"),i.classList.remove("active")):(t.classList.add("active"),i.classList.add("active"))}),c.forEach(e=>{e.addEventListener("change",function(){t.innerHTML=this.getAttribute("data-name"),t.classList.remove("active"),i.classList.remove("active")})}),document.addEventListener("click",function(e){e.target.classList.contains("custom-select__head")||(t.classList.remove("active"),i.classList.remove("active"))})}function customFile(e){let t=e.querySelectorAll("input")[0],i=e.querySelectorAll(".custom-file__name")[0];t.addEventListener("change",function(){i.innerHTML=this.files[0].name})}window.addEventListener("load",function(){document.querySelectorAll(".js-no-ui-slider").forEach(e=>{rangeSlider(e)}),document.querySelectorAll(".js-custom-select").forEach(e=>{customSelect(e)}),document.querySelectorAll(".js-custom-file").forEach(e=>{customFile(e)})});